    sortFn:Function
    public sort(srcArr:Array,sortFun:Function):T[]{

        //根据数据srcArr的长度，确定增长量h的初始值步增：2h+1
        let h:number = 1;
        while(h=1) {
            for(let i = h;i=h; j-=h) {
                    if(this.greater(srcArr[j-h],srcArr[j])) { //如果j-h位置上的元素大于j位置上的元素则进行交换
                        this.exch(srcArr,j-h,j)
                    }else { //否则结束循环
                        break
                    }
                }
            }
            h = Math.floor(h/2)
        }

        return  srcArr;
    }
    private greater(a:T,b:T) {
        if(this.sortFn) {
            return this.sortFn(a,b)
        }else {
            return  a >= b ? 1 :  0
        }
    }
    private exch(srcArr:Array,i:number,j:number) {
        let temp = srcArr[i]
        srcArr[i] = srcArr[j]
        srcArr[j] = temp;
    }
}

